import React from 'react'
import styled, { css } from 'styled-components'

import { Download } from '../Svg'

import Greetings from '../../utils/greetings'
import { storeImage } from '../../utils/FirebaseRequest';

const Container = styled.div`
  align-items: center;
  display: flex;
  justify-content: space-between;
  padding: 32px 0;
  margin: 32px 0;

  ${(props) => props.size.width < 768 && css`
    justify-content: center;
  `}
`

const ProfileContainer = styled.div`
  flex-direction: column;

  ${(props) => props.size.width < 768 && css`
    text-align: center;
  `}
`

const Greeting = styled.p`
  font-weight: 400;
`

const Name = styled.h1`
  margin-block-end: 0;
  margin-block-start: 0;
  transition: ${({ theme }) => theme.transition};
`

const FullName = styled.span`
  color: ${({ theme }) => theme.primary};
  margin-left: 8px;
  transition: ${({ theme }) => theme.transition};
  width: 50px;

  ${(props) => props.size.width < 768 && css`
    width: auto;
  `}
`

const Profession = styled.p`
  font-size: 20px;
`

const DownloadCV = styled.button`
  align-items: center;
  background: ${({ theme }) => theme.background};
  border: 1px solid ${({ theme }) => theme.primary};
  border-radius: 4px;
  cursor: pointer;
  display: flex;
  outline: none;
  padding: 8px 16px;
  transition: ${({ theme }) => theme.transition};

  ${(props) => props.size.width < 768 && css`
    margin: 0 auto;
  `}
`

const DownloadText = styled.span`
  color: ${({ theme }) => theme.primary};
  font-family: Poppins;
  font-size: 16px;
  font-weight: 400;
  margin-left: 16px;
  transition: ${({ theme }) => theme.transition};
`

const ImageContainer = styled.div`
  height: 400px;
  margin-right: 64px;
  width: 400px;
  `

const Img = styled.img`
  border-radius: 8px;
  height: 100%;
  object-fit: cover;
  width: 100%;
`

const Header = ({ size, theme }) => {
  // const handleStoreImage = async (e) => {
  //   await storeImage(e.target.files[0])
  // }

  return (
    <Container size={size}>
      <ProfileContainer size={size}>
        <Greeting>Good Ohayou</Greeting>
        <Name>
          I'm
      <FullName size={size}>Reza Fahlevi</FullName>
        </Name>
        <Profession>Jagoan Neon</Profession>
        {/* {data.show_cv && (
          <DownloadCV size={size} onClick={() => window.open(data.cv)}>
            <Download fill={theme.primary} />
            <DownloadText>Download CV</DownloadText>
          </DownloadCV>
        )} */}
      </ProfileContainer>
      {size.width >= 768 &&
        <ImageContainer>
          {/* <Img alt={data.name} src={'https://firebasestorage.googleapis.com/v0/b/site-randy.appspot.com/o/avatar%2Fbandy.png?alt=media&token=ba3bab28-929d-4ec1-acb0-0a25450f656a'} /> */}
          <Img src={require('images/vector.png)}')}/>
        </ImageContainer>
      }
      {/* <input type='file' onChange={handleStoreImage} /> */}
    </Container>
  )
}

export default Header
